mapper.insert:
insert into project (name, keywords, create_time
  )
values (#{name,jdbcType=VARCHAR}, #{keywords,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update project
set name = #{name,jdbcType=VARCHAR},
  keywords = #{keywords,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, keywords, create_time
from project
where id = #{id,jdbcType=INTEGER}
